//Import gzipy
var gzipy = require('../index.js');

//Compress the file
gzipy.compress('./file.txt', 'file.txt.gz', function(error)
{
  //Check the error
  if(error){ return console.error(error.message); }

  //Display in console
  console.log('File compressed as file.txt.gz');

  //Decompress the file
  gzipy.decompress('./file.txt.gz', './file2.txt', function(error)
  {
    //Check the error
    if(error){ return console.error(error.message); }

    //Display in console
    console.log('File decompressed as file2.txt');
  });
});